import './App.css'; //styling
import React, { useEffect } from 'react';
import AddToCapsule from './components/AddToCapsule'; //importing pages
import ViewCapsule from './components/ViewCapsule';
import MyForm from './components/MyForm'


function App() {


  let [currPage, setPage] = React.useState('home') //starts on home page
  if(currPage === 'home'){
    //html of home page
    return (
      <div className="App">
        <h1>
          Track your Diet!
        </h1> 
        <p> This app helps keep track of your nutritional intake throughout the day! 
         Any fitness professional will let you know that nutrition is important to any fitness goal, from losing weight to adding muscle
         While keeping track of how much you are eating can be stressfull and tough for busy individuals, this app does the hard work for you!</p>
        <p> Here is how you use it:</p>

        <button className='button button1' onClick={() => setPage('addtocapsule')}>
          Add meal 
        </button>
        <p> After every meal, input the nutritional facts by clicking the 'Add meal' button
         (while recommended in grams, measurement unit is arbitrary) </p>
        <h1> "View All Meals:"</h1>
        <p> Allows you to view all meals in a specific time frame, and even remove certain meals.</p>
        <h1>"View daily nutrition"</h1>
        <p> Tracks and displays your nutrition for each individual day. Dates that are displayed can be filtered by you.</p>

        <button className='button button1' onClick={() => setPage('viewcapsule')}>
          View all meals
        </button>
        <button className='button button1' onClick={() => setPage('MyForm')}>
         View daily nutrition
        </button>



      </div>

    );
  }
  else if (currPage === 'addtocapsule'){// changes pages after button click

    return (
      <AddToCapsule setPage = {setPage}/>
    );
  }
  else if (currPage === 'viewcapsule'){
    return (
      <ViewCapsule setPage = {setPage}/>
    );
  }
  else if (currPage === 'MyForm'){
    return (
      <MyForm setPage = {setPage}/>
    );
  }

  else{
    return (
      <div>
        refresh page if here
      </div>
    );
  }
}

export default App;
